INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Mass Effect Trilogy', 4.8, 2012, 'Role-playing (RPG), Shooter', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/1.png', 'Mass Effect: Uma trilogia de RPG espacial. Salve a galáxia como Comandante Shepard. Escolhas, relacionamentos e ação intensa.', 'A trilogia Mass Effect é uma épica série de jogos de RPG de ação ambientada em um vasto universo de ficção científica. Combinando uma história cativante, personagens memoráveis ​​e jogabilidade envolvente, os jogadores embarcam em uma jornada intergaláctica como o Comandante Shepard. O destino da galáxia está em jogo enquanto Shepard luta contra uma ameaça antiga conhecida como os Reapers, enfrentando escolhas difíceis e construindo alianças ao longo do caminho. A trilogia Mass Effect é aclamada por sua narrativa emocionalmente impactante, mecânica de jogo aprofundada e o poder de moldar o próprio futuro da galáxia.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Red Dead Redemption 2', 4.7, 2018, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/2.png', 'Red Dead Redemption 2: um conto épico do Velho Oeste.', 'Red Dead Redemption 2 é um jogo de ação e aventura ambientado no final do século XIX, durante a era do Velho Oeste americano. Desenvolvido pela Rockstar Games, o jogo segue a história de Arthur Morgan, um fora da lei e membro de uma gangue de bandidos liderada por Dutch van der Linde. Os jogadores exploram um vasto mundo aberto, repleto de paisagens deslumbrantes, cidades movimentadas e vastas áreas selvagens. Com uma narrativa profunda, personagens complexos e uma jogabilidade imersiva, Red Dead Redemption 2 oferece uma experiência envolvente e emocionante, cheia de escolhas morais e consequências significativas.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Witcher 3: Wild Hunt', 4.7, 2014, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/3.png', 'The Witcher 3: Wild Hunt - Uma aventura de RPG épica com combates emocionantes, escolhas morais e um mundo aberto detalhado.', 'The Witcher 3: Wild Hunt é um jogo de RPG de ação baseado na famosa série de livros de fantasia de Andrzej Sapkowski. Desenvolvido pela CD Projekt Red, o jogo coloca os jogadores no papel de Geralt de Rivia, um caçador de monstros conhecido como "witcher". Em um mundo aberto vasto e detalhado, os jogadores embarcam em uma busca épica repleta de escolhas morais, combates emocionantes e histórias envolventes. Com gráficos impressionantes, um sistema de combate desafiador e um elenco de personagens carismáticos, The Witcher 3: Wild Hunt oferece uma experiência imersiva e cativante, repleta de magia, criaturas fantásticas e intrigas políticas. É considerado um dos melhores jogos de todos os tempos, conquistando tanto os fãs da série de livros quanto os jogadores em geral.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sekiro: Shadows Die Twice', 3.8, 2019, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/4.png', 'Sekiro: Shadows Die Twice - Combates intensos, habilidades ninjas e um Japão feudal implacável.', 'Sekiro: Shadows Die Twice é um jogo de ação e aventura desenvolvido pela FromSoftware. Ambientado no Japão feudal, os jogadores assumem o papel de Sekiro, um guerreiro ninja com habilidades mortais. O jogo apresenta uma jogabilidade desafiadora e intensa, com combates rápidos e precisos, além de um sistema de morte e ressurreição único. Os jogadores devem dominar a técnica de combate "Postura" para derrotar inimigos formidáveis ​​e enfrentar chefes poderosos em um mundo cheio de mistérios e perigos. Com gráficos impressionantes e uma atmosfera sombria, Sekiro: Shadows Die Twice oferece uma experiência emocionante para os fãs de jogos desafiadores e imersivos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ghost of Tsushima', 4.6, 2012, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/5.png', 'Ghost of Tsushima: Honra e combate samurai em um Japão feudal épico.', 'Ghost of Tsushima é um jogo de ação e aventura desenvolvido pela Sucker Punch Productions. Ambientado no Japão feudal do século XIII, o jogo segue a jornada do samurai Jin Sakai, em sua luta para proteger a ilha de Tsushima da invasão mongol. Os jogadores exploram um vasto mundo aberto, repleto de paisagens deslumbrantes, templos antigos e campos de batalha épicos. Com combates estratégicos, uso de táticas furtivas e um sistema de personalização de habilidades, os jogadores podem escolher entre seguir o caminho do samurai honrado ou se tornar um "fantasma", um guerreiro das sombras que usa truques e táticas engenhosas. Com visuais impressionantes, uma trilha sonora envolvente e uma história cativante, Ghost of Tsushima oferece uma experiência imersiva e emocionante, repleta de honra, sacrifício e vingança.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Super Mario World', 4.7, 1990, 'Platform', 'Super Ness, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/6.png', 'Super Mario World: Aventura icônica de Mario para salvar a Princesa Peach.', 'Super Mario World é um jogo de plataforma clássico desenvolvido pela Nintendo. Lançado originalmente para o Super Nintendo Entertainment System (SNES), o jogo segue as aventuras do famoso encanador Mario e seu fiel companheiro, Luigi, em sua jornada pela Ilha dos Dinossauros para resgatar a Princesa Peach das garras do malvado Bowser. Com gráficos coloridos, níveis criativos e uma trilha sonora memorável, Super Mario World é uma experiência divertida e envolvente. Os jogadores exploram diferentes fases, enfrentam inimigos icônicos, coletam moedas e power-ups, e desvendam segredos enquanto avançam pelo jogo. Com sua jogabilidade acessível e cativante, Super Mario World continua sendo um dos jogos mais amados e influentes da história dos videogames.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Hollow Knight', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/7.png', 'Hollow Knight: Exploração sombria e desafiadora em um mundo encantado.', 'Hollow Knight é um jogo indie de ação e aventura desenvolvido pela Team Cherry. Ambientado em um mundo sombrio e misterioso chamado Hallownest, o jogo segue as aventuras de um cavaleiro sem nome em busca de respostas e redenção. Os jogadores exploram um vasto labirinto subterrâneo, enfrentando criaturas perigosas e chefes desafiadores. Com sua estética única de arte desenhada à mão, trilha sonora envolvente e jogabilidade metroidvania, Hollow Knight oferece uma experiência atmosférica e imersiva. Os jogadores descobrem segredos, desbloqueiam habilidades especiais e desvendam a história sombria e intrigante do mundo de Hallownest. Com sua dificuldade gratificante e personagens cativantes, Hollow Knight conquistou o coração dos jogadores e se tornou um clássico moderno dos jogos independentes.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ori and the Blind Forest', 4, 2015, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/8.png', 'Ori and the Blind Forest: Uma jornada emocional e encantadora em um mundo mágico.', 'Ori and the Blind Forest é um jogo de plataforma e aventura desenvolvido pela Moon Studios. O jogo conta a emocionante história de Ori, uma pequena criatura espiritual, em sua jornada para salvar a floresta de Nibel. Com belos visuais desenhados à mão, uma trilha sonora encantadora e jogabilidade fluida, Ori and the Blind Forest oferece uma experiência cativante e emocional. Os jogadores exploram um mundo vibrante, resolvem quebra-cabeças e enfrentam perigos para restaurar a luz e a vida à floresta. Com sua narrativa envolvente e personagens adoráveis, o jogo transmite uma mensagem de amor, amizade e superação. Ori and the Blind Forest é um verdadeiro deleite para os fãs de jogos de plataforma atmosféricos e emocionantes.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Cuphead', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/9.png', 'Cuphead: Estilo de desenho animado dos anos 1930 em uma aventura desafiadora.', 'Cuphead é um jogo de plataforma e ação desenvolvido pela Studio MDHR. Inspirado nas animações da década de 1930, o jogo apresenta um estilo visual único em desenhos animados e uma trilha sonora de jazz cativante. Os jogadores assumem o papel de Cuphead ou Mugman, dois personagens que devem enfrentar uma série de desafios e batalhas contra chefes para pagar uma dívida com o diabo. Com uma dificuldade desafiadora e mecânicas de jogo precisas, Cuphead oferece uma experiência empolgante e gratificante. Os jogadores enfrentam combates intensos, desvendam segredos e desfrutam da atmosfera retrô enquanto embarcam em uma jornada emocionante e visualmente impressionante. Cuphead é um verdadeiro tributo aos jogos clássicos e à era de ouro da animação.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sonic CD', 4, 1993, 'Platform', 'Sega CD, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/10.png', 'Sonic CD: Viagem no tempo em uma aventura de plataforma com o ouriço azul mais rápido.', 'Sonic CD é um jogo de plataforma desenvolvido pela SEGA. Lançado originalmente em 1993, o jogo apresenta as aventuras do famoso ouriço azul, Sonic, em sua luta contra o vilão Dr. Robotnik. Sonic CD se destaca por suas fases únicas e pelo uso do conceito de viagem no tempo. Os jogadores exploram diferentes zonas do passado, presente e futuro, coletando anéis, derrotando inimigos e enfrentando chefes desafiadores. Com sua jogabilidade rápida e fluente, gráficos coloridos e uma trilha sonora icônica, Sonic CD é um clássico do gênero plataforma. O jogo captura a essência da era dos 16 bits e oferece uma experiência divertida e nostálgica para os fãs do ouriço mais rápido dos videogames.');
